package Javabasic;

public class Forloops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//For loop
		
		
		
		
		//while loop 
		
		int i=1; int n=5;
		while (i<=n) {
			System.out.println(i);
		
		i++;
		
	
		}
		
		
		//Do while loop 
		System.out.println("inside the do while loop");
		int x=1; int y=5;
		
		do {
			
			System.out.println(x);
			x++;
			
		}while(x<=y); 
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
